import React, { useEffect, useState } from 'react';
import Swiper from 'swiper';
import SwiperCore from 'swiper';
import { Navigation, Pagination, Mousewheel} from 'swiper/modules';

import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/mousewheel';

import HomePage from './Homepage';
import ProblemPage from './ProblemPage';
import SolutionPage from './SolutionPage';
import AlliancePage from './AlliancePage';
import ContactPage from './ContactPage';

const GalleryComponent = () => {
  const [swiperInstance, setSwiperInstance] = useState(null);

  useEffect(() => {
    SwiperCore.use([Navigation, Pagination, Mousewheel]);

    const swiperOptions = {
      speed: 1000,
      effect: 'slide',
      loop: true,
      mousewheel: {
        enabled: true,
        invert: false,
        sensitivity: 100,
      },
    };

    const horizontalSwiper = new Swiper('.swiper-container-horizontal', {
      ...swiperOptions,
      direction: 'horizontal',
    });

    const verticalSwiper = new Swiper('.swiper-container-vertical', {
      ...swiperOptions,
      direction: 'vertical',
    });

    setSwiperInstance(window.innerWidth <= 768 ? verticalSwiper : horizontalSwiper);

    const handleResize = () => {
      setSwiperInstance(window.innerWidth <= 768 ? verticalSwiper : horizontalSwiper);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      horizontalSwiper.destroy(true, true);
      verticalSwiper.destroy(true, true);
    };
  }, []);

  return (
    <div>
      <section>
        {swiperInstance && (
          <div className={`swiper-container swiper-container-${swiperInstance.params.direction}`}>
            <div className="swiper-wrapper">
              <div className="swiper-slide">
                <HomePage />
              </div>
              <div className="swiper-slide">
                <ProblemPage />
              </div>
              <div className="swiper-slide">
                <SolutionPage />
              </div>
              <div className="swiper-slide">
                <AlliancePage />
              </div>
              <div className="swiper-slide">
                <ContactPage />
              </div>
            </div>
          </div>
        )}
      </section>
    </div>
  );
};

export default GalleryComponent;
