import React, { useEffect, useRef } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/all'; // Import ScrollTrigger from GSAP

gsap.registerPlugin(ScrollTrigger);

const TextEffectComponent = () => {
  const textRef = useRef(null);

  useEffect(() => {
    // Animation logic using GSAP and ScrollTrigger
    const textElement = textRef.current;

    gsap.to(textElement, {
      backgroundSize: '100%',
      ease: 'none',
      scrollTrigger: {
        trigger: textElement,
        start: 'center 80%',
        end: 'center 20%',
        scrub: true,
      },
    });
  }, []); // Empty dependency array ensures this effect runs only once after the initial render

  return (
    <div className="container">
      <h1 ref={textRef} className="text">TEXT EFFECT<span>WOAH</span></h1>
      {/* ... (other h1 elements) */}
    </div>
  );
};

export default TextEffectComponent;
