import React, { useEffect, useRef } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/all'; // Import ScrollTrigger from GSAP

gsap.registerPlugin(ScrollTrigger);

const TextEffectComponent = () => {
  const textRef = useRef(null);

  useEffect(() => {
    // Animation logic using GSAP and ScrollTrigger
    const textElement = textRef.current;

    gsap.to(textElement, {
      backgroundSize: '100%',
      ease: 'none',
      scrollTrigger: {
        trigger: textElement,
        start: 'center 80%',
        end: 'center 20%',
        scrub: true,
      },
    });
  }, []);

  return (
    <div className="container">
      <h1 className="text">WELCOME TO<span>BUILDING</span></h1>
      <h1 className="text">EVERMORROW<span>FOR THE</span></h1>
      <h1 className="text">LABS<span>DAY AFTER</span></h1>
    </div>
  );
};

export default TextEffectComponent;
