import React, { useEffect } from 'react';
import Swiper from 'swiper';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/mousewheel';

import TextEffectComponent from './TextEffectComponent';
import ContentComponent from './ContentComponent';

import gsap from 'gsap';
import ScrollTrigger from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);


const GalleryComponent = () => {
    useEffect(() => {
        const slides = gsap.utils.toArray('.swiper-slide');
        const container = document.querySelector('.swiper-container');

        const swiperAnimation = gsap.to(slides, {
          xPercent: -100 * (slides.length - 1),
          ease: 'none',
          scrollTrigger: {
            trigger: container,
            pin: true,
            scrub: true,
            snap: 1 / (slides.length - 1),
            end: () => `+=${container.offsetWidth * (slides.length - 1)}`,
          },
        });

        return () => {
          swiperAnimation.kill();
        };
      }, []);

  return (
    <div>
      <section>
        {/* <img
          className="hero"
          src="https://github.com/ecemgo/mini-samples-great-tricks/assets/13468728/c62efd51-f844-4335-ba29-70ea35521dd8"
          alt=""
        /> */}
        <div className="swiper">
          <div className="swiper-wrapper">
            <div className="swiper-slide">
              <TextEffectComponent/>
            </div>

            <div className="swiper-slide">
                <ContentComponent/>
            </div>

            <div className="swiper-slide">
              <h1 className="title three">Paris</h1>
              <img
                src="https://github.com/ecemgo/mini-samples-great-tricks/assets/13468728/b49c729f-65b0-4a2a-baf7-1d9f180df0a6"
                alt=""
              />
            </div>

            <div className="swiper-slide">
              <h1 className="title four">Brugge</h1>
              <img
                src="https://github.com/ecemgo/mini-samples-great-tricks/assets/13468728/c9a61071-c74d-4e8b-966a-6948cdbd9665"
                alt=""
              />
            </div>

            <div className="swiper-slide">
              <h1 className="title five">Bavaria</h1>
              <img
                src="https://github.com/ecemgo/mini-samples-great-tricks/assets/13468728/25ab6cb7-9251-4aa5-9993-32ebd8542616"
                alt=""
              />
            </div>

            <div className="swiper-slide">
              <h1 className="title six">Copenhagen</h1>
              <img
                src="https://github.com/ecemgo/mini-samples-great-tricks/assets/13468728/365656bc-24bc-4d95-92da-7f34faa616cf"
                alt=""
              />
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default GalleryComponent;
