import React, { useEffect, useRef } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/all'; // Import ScrollTrigger from GSAP

gsap.registerPlugin(ScrollTrigger);

const TextEffectComponent = () => {
  const textRef = useRef(null);

  useEffect(() => {
    // Animation logic using GSAP and ScrollTrigger
    const textElement = textRef.current;

    gsap.to(textElement, {
      backgroundSize: '100%',
      ease: 'none',
      scrollTrigger: {
        trigger: textElement,
        start: 'center 80%',
        end: 'center 20%',
        scrub: true,
      },
    });
  }, []);

  return (
    <div className="container">
      <h1 className="text">TEXT EFFECT<span>WOAH</span></h1>
      <h1 className="text">GSAP<span>AND CLIPPING</span></h1>
      <h1 className="text">CRAZYYY<span>CRAZYYY</span></h1>
      <h1 className="text">
        HOVER ON ME<span><a href="https://stacksorted.com/text-effects/minh-pham" target="_blank" rel="noopener noreferrer">SOURCE</a></span>
      </h1>
      <h1 className="text">
        LIKE THIS?<span><a href="https://twitter.com/juxtopposed" target="_blank" rel="noopener noreferrer">LET'S CONNECT</a></span>
      </h1>
    </div>
  );
};

export default TextEffectComponent;
